import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      gameboard: [null, null, null, null, null, null, null, null, null],
      currentPlayer: "ðŸ£"
    }
  }

  squareClick = (currentBox) => {
    let currentPlayer = this.state.currentPlayer === "ðŸ£" ? "ðŸ¥‘" : "ðŸ£"
    this.state.gameboard[currentBox] = this.state.currentPlayer
    this.setState({
      gameboard: this.state.gameboard,
      currentPlayer: currentPlayer
    })
  }

  render(){
    let mappedGameboard = this.state.gameboard.map((value, index) => {
      return(
        <Square
          value={ value }
          index={ index }
          key={ index }
          squareClick={ this.squareClick }
        />
      )
    })
    return(
      <React.Fragment>
        <h1>Tic Tac Toe</h1>
        <div id="gameboard">
          { mappedGameboard }
        </div>
      </React.Fragment>
    )
  }
}
export default App
