import React, { Component } from 'react'
import Square from './components/Square'
import './App.css'

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      gameboard: Array(9).fill(null)
    }
  }

  render(){
    let mappedGameboard = this.state.gameboard.map((value, index) => {
      return(
        <Square
          value={ value }
          index={ index }
          key={ index }
        />
      )
    })
    return(
      <React.Fragment>
        { mappedGameboard }
      </React.Fragment>
    )
  }
}
export default App
